import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { Producto } from 'src/app/interfaces/producto';
import { ProductoService } from 'src/app/servicio/producto.service';
import { ActivatedRoute, Router } from '@angular/router';
import { IfStmt } from '@angular/compiler';

@Component({
  selector: 'app-agg-edit-productos',
  templateUrl: './agg-edit-productos.component.html',
  styleUrls: ['./agg-edit-productos.component.scss'],
})
export class AggEditProductosComponent implements OnInit {
  form: FormGroup;
  id: number;
  titulo: string = 'Agregar';

  constructor(
    private fm: FormBuilder,
    private _productoServicio: ProductoService,
    private router: Router,
    private aRouter: ActivatedRoute
  ) {
    //Validaciones
    // si se desea mas de 1 validacion hacerlo como un array
    this.form = this.fm.group({
      nombre: ['', Validators.required],
      descripcion: ['', Validators.required],
      precio: ['', Validators.required],
      stock: ['', Validators.required],
    });

    this.id = Number(this.aRouter.snapshot.paramMap.get('id'));
    console.log(this.id);
  }

  ngOnInit(): void {
    if (this.id != 0) {
      this.titulo = 'Editar';
      this.EditarProducto(this.id);
    }
  }

  addProducto() {
    try {
      const productos: Producto = {
        nombre: this.form.get('nombre')?.value,
        descripcion: this.form.get('descripcion')?.value,
        precio: this.form.get('precio')?.value,
        stock: this.form.get('stock')?.value,
      };

      if (this.id != 0) {
        productos.id = this.id;
        this._productoServicio
          .updateProducto(this.id, productos)
          .subscribe(() => {
            console.log('Producto fue actualiado con exito');
          });
        this.router.navigate(['/']);
      } else {
        this._productoServicio.createProducto(productos).subscribe(() => {
          console.log('Producto agregado con exito');
          this.router.navigate(['/']);
        });
      }
    } catch {
      console.log('error al agregar');
    }
  }

  //Editar Producto
  EditarProducto(id: number) {
    this._productoServicio.dataProducto(id).subscribe((data: Producto) => {
      this.form.setValue({
        nombre: data.nombre,
        descripcion: data.descripcion,
        precio: data.precio,
        stock: data.stock,
      });
    });
  }
}
